<?phpnamespace App\Helpers;class NanoSatisticProcessing{    /**     * @var false|null|string     */    protected $from = null;    /**     * @var false|null|string     */    protected $to = null;    /**     * @var null     */    protected $group;    /**     * @var bool     */    protected $withoutGroup = false;    /**     * @var     */    protected $data;    public function __construct($group = null, string $from = null, string $to = null) {        if($from && $to) {            $this->from = date('Y-m-d 00:00:00', strtotime($from));            $this->to = date('Y-m-d 23:59:59', strtotime($to));        }        $this->group = $group;    }    public function setWithoutGroup() {        $this->withoutGroup = true;    }    /**     * @return array     */    public function getStatisticInformation($addresses,$fromDate, $toDate) {                if (trim($fromDate) != '' && trim($toDate) != '') {            list($m, $d, $y) = explode("/", $fromDate);            $fromDate1 = "$y-$m-$d " . date('H:i:00');            list($m, $d, $y) = explode("/", $toDate);            $toDate1 = "$y-$m-$d " . date('H:i:00');            $fromDate1 = new \DateTime($fromDate1);            $toDate1 = new \DateTime($toDate1);            $diff = $toDate1->diff($fromDate1);            if($diff->d==0&&$diff->m>0){                $hours = $diff->days * 24;            }            else{                $hours = $diff->d * 24;            }        } else {            $hours = '';        }        if($this->group && !$this->withoutGroup) {            $this->getDataForCurrentGroupWallets();        }        elseif ($this->withoutGroup) {            $this->getDataForNonGroupMachines();        }        else {            $this->getDataForAllWallets();        }        return [            'totalShares' => $this->getTotalShares($addresses,$fromDate, $toDate),            'avgHashrate' => $this->getAvgHashrate($addresses,$hours),            'onlineTime' => $this->getOnlineTime($addresses,$fromDate, $toDate),            'offlineTime' => $this->getOfflineTime($addresses,$fromDate, $toDate),        ];    }    protected function getDataForNonGroupMachines() {        $groups = app('db')->select(sprintf('select DISTINCT(machine_id) from `groups2machine`'));        $groups = collect($groups)->map(function($x){            return $x->machine_id;        })->toArray();        if(!$this->from && !$this->to) {            $sql = sprintf('select * from `data` where `machine_id` NOT IN ("%s")', implode('","', $groups));        }        else {            $sql = sprintf("select * from `data` where (`date` BETWEEN '%s' AND '%s') AND `machine_id` NOT IN ('%s')", implode("', '", $groups));        }        $this->data = app('db')->select($sql);    }    /**     * @return float|int     */    protected function getOnlineTime($addresses,$fromDate, $toDate) {        $anyActivity = [];        foreach ($this->data as $datum) {            $anyActivity[] = $datum;        }        return round(sizeof($anyActivity) * 10 / 60, 2);    }    /**     * @return float|int     */    protected function getOfflineTime($addresses,$fromDate, $toDate) {        $nonActivity = [];        foreach ($this->data as $datum) {            if($datum->shares === 0) {                $nonActivity[] = $datum;            }        }        return round(sizeof($nonActivity) * 10 / 60, 2);    }    protected function getAvgHashrate($addresses,$hours) {        $avgHashRate = 0;        foreach ($addresses as $address) {            if (is_numeric($hours)) {                $data = file_get_contents("https://api.nanopool.org/v1/eth/avghashratelimited/$address/$hours");                $temp = json_decode($data, true);                if ($temp['status'] == FALSE) {                    $data = file_get_contents("https://api.nanopool.org/v1/eth/avghashrate/$address");                }            } else {                $data = file_get_contents("https://api.nanopool.org/v1/eth/avghashrate/$address");            }            $temp = json_decode($data, true);            if ($temp['status'] == true) {                if (is_array($temp['data'])) {                    $avgHashRate += round((float) (array_sum($temp['data']) / count($temp['data'])), 2);                } else {                    $avgHashRate += round((float) $temp['data'], 2);                }            } else {                $avgHashRate += 0;            }        }                return $avgHashRate;        $avgHashRate = [];        foreach ($this->data as $datum) {            $avgHashRate[] = $datum->hashrate;        }        if($avgHashRate) {            $avgHashRate = round(array_sum($avgHashRate)/sizeof($avgHashRate), 2);        }        else {            $avgHashRate = 0;        }        return $avgHashRate;    }    protected function getTotalShares($addresses,$fromDate, $toDate) {        if (trim($fromDate) != '' && trim($toDate) != '') {            list($m, $d, $y) = explode("/", $fromDate);            $fromDate = strtotime("$y-$m-$d");            list($m, $d, $y) = explode("/", $toDate);            $toDate = strtotime("$y-$m-$d");        }                $totalShares = 0;                foreach ($addresses as $address) {            $data = file_get_contents("https://api.nanopool.org/v1/eth/shareratehistory/$address");            $temp = json_decode($data, true);            $data = $temp['data'];            foreach ($data as $temp) {                $date = date("Y-m-d", $temp['date']);                $date = strtotime($date);                if (is_numeric($fromDate) && is_numeric($toDate)) {                    if ($date >= $fromDate && $date <= $toDate) {                        $totalShares += $temp['shares'];                    }                } else {                    $totalShares += $temp['shares'];                }            }        }        return $totalShares;        foreach ($this->data as $datum) {            $totalShares += $datum->shares;        }        return $totalShares;    }    /**     * get data     */    protected function getDataForAllWallets() {        if(!$this->from && !$this->to) {            $sql = sprintf('select * from `data`');        }        else {            $sql = sprintf("select * from `data` where `date` BETWEEN '%s' AND '%s'", $this->from, $this->to);        }        $this->data = app('db')->select($sql);    }    /**     * get data     */    protected function getDataForCurrentGroupWallets() {        $groups = app('db')->select(sprintf('select DISTINCT(machine_id) from `groups2machine` WHERE group_id = %d', $this->group));        $groups = collect($groups)->map(function($x){            return $x->machine_id;        })->toArray();        if(!$this->from && !$this->to) {            $sql = sprintf('select * from `data` WHERE machine_id IN ("%s")', implode('", "', $groups));        }        else {            $sql = sprintf("select * from `data` where `date` BETWEEN '%s' AND '%s' AND machine_id IN ('%s')", $this->from, $this->to, implode("', '", $groups));        }        $this->data = app('db')->select($sql);    }}